// Utility functions
export const getColor = (family: string, shade: string | number): string | undefined => {
  const colorFamily = colors[family as keyof typeof colors];
  if (colorFamily && typeof colorFamily === 'object') {
    return (colorFamily as Record<string, string>)[shade.toString()];
  }
  return undefined;
};

